<?php// Example controllerclass MainController extends WeeController {  public function __construct() {    parent::__construct();    // Add this style sheet to every template rendered within the class    $this->view->addToHeader('css/style.css');  }  public function index() {    $welcomeVar = 'Hello World !';    $setupDir = '';    if(file_exists(SERVER_ROOT . DS . "install")){      $setupDir = '[For security reasons, please remove the installation directory (install) from your server]';    }    $this->view->set('hello', $welcomeVar);    $this->view->set('setupDir', $setupDir);    echo $this->view->render('index');  }}